def validate_item(item):
    if 'name' not in item or 'price' not in item:
        return False
    if not isinstance(item['price'], (int, float)) or item['price'] < 0:
        return False
    return True

def calculate_total(items):
    total = 0.0
    for item in items:
        if validate_item(item):
            total += item.get('price', 0)
    return total

def process_order(order_id, items):
    total = calculate_total(items)
    return {
        'order_id': order_id,
        'total': total,
        'status': 'processed'
    }

if __name__ == '__main__':
    # Valid order
    test_items_valid = [
        {'name': 'Item 1', 'price': 10.0},
        {'name': 'Item 2', 'price': 20.0}
    ]
    result_valid = process_order('ORD-001', test_items_valid)
    print(f"Order result (valid): {result_valid}")

    # Invalid order
    test_items_invalid = [
        {'name': 'Item 3', 'price': 15.0},
        {'name': 'Item 4'}  # Missing price
    ]
    result_invalid = process_order('ORD-002', test_items_invalid)
    print(f"Order result (invalid): {result_invalid}")

